import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

import { TextField, Button } from '@material-ui/core';

function App() {

    let data =[
        {
            name: "Devon",
            tasks: [
                {
                    "taskName": "mow the grass",
                    "isComplete": false
                },
                {
                    "taskName": "eat some tea",
                    "isComplete": true
                },
                {
                    "taskName": "cry",
                    "isComplete": false
                }
            ]
        },
        {
            name: "Johnny",
            tasks: [
                {
                    "taskName": "mow the grass",
                    "isComplete": false
                },
                {
                    "taskName": "eat some tea",
                    "isComplete": true
                },
                {
                    "taskName": "cry",
                    "isComplete": false
                }
            ]
        }
    ]

    const [usersTasks, setUsersTasks] = useState({});
    const [myTasks, setMyTasks] = useState(null);
    const [signedInUser, setSignedInUser] = useState('Devon');

    useEffect(() => {
        setMyTasks(data);
    }, [])

    useEffect(() => {
        console.log(myTasks)
    }, [myTasks])


    return (
        <div className="App">
            {myTasks.forEach((user) => {
                <p>
                    {user.name}
                </p>

                {myTasks[0].tasks.map((task) => {
                        return (
                            <p>
                                {task.taskName}
                            </p>
                        );
                    })

                    signedInUser === user.name ?
                    <span>
                        <TextField id="filled-basic" label="Filled" variant="filled" />
                            <Button>
                                Send
                            </Button>
                    </span>
                : null}
            })}

        </div>
    );
}

export default App;
